<template>    <div>        <header>            <div class="headline">                <a>                    <strong>FindHopes</strong>                </a>            </div>        </header>        <div class="button-back" style="margin-top:80px;"><router-link :to="{ name:'projects' }"><sgrong>Back</sgrong></router-link></div>        <div>            <br>            <div class="mainTitle">                <br>                <div class="line">                    <div class="fontStyle">                        <h3>My Information</h3>                        <hr>                    </div>                </div>                <br>                <div class="tab-content1">                    <div role="tabpannel" class="tab-pane fade show active" id="profile">                        <br>                        <div class="line" style="height:50px;margin-left:50px;">                                Id:{{login_user_id}}                        </div>                        <div class="line" style="height:50px;margin-left:50px;">                                Name:{{login_username}}                        </div>                        <div class="line" style="height:50px;margin-left:50px;">                                Email:{{login_user_email}}                        </div>                        <div class="line" style="height:50px;margin-left:50px;">                                Address:{{login_user_location}}                        </div>                        <div class="line">                            <div>                                <button type="button" class="button-login" onclick="window.location.href='http://localhost:8080/createProject'">                                    Create Project                                </button>                            </div>                            <div>                                <button type="button" class="button-login" @click="view_userProject()">                                    My Project                                </button>                            </div>                            <div>                                <button type="button" class="button-login" @click="view_userPledge()">                                    My Pledge                                </button>                            </div>                            <div>                                <button type="button" class="button-login" @click="log_out()">                                    Logout                                </button>                            </div>                        </div>                        <br>                    </div>                </div>                <br>                <br>            </div>        </div>        <router-view></router-view>    </div></template><script>    export default {        data() {            return {                login_username:this.$session.get('username'),                login_user_id: '',                login_user_email: '',                login_user_location: '',                my_pledges: [],                empty_flag: 0,                my_projects: [],                empty_flag1:0,                test: this.islogin            }        },        mounted: function () {            this.get_login_user();        },        methods: {            log_out() {                console.log(this.$session.get('token'));                this.$http.post('http://csse-s365.canterbury.ac.nz:5199/api/v2/users/logout/',                    {},                    {                        headers:                            {                                'X-Authorization': this.$session.get('token')                            },                    },                ).then(function(res){                    this.$router.push({path: '/'});                    window.sessionStorage.setItem('islogin',false);                    this.$session.destroy();                }, function (err) {                    console.log(err);                })            },            createProject() {                this.$router.push({path: '/createProject'});            },            userProject() {                this.$router.push({path: '/userProject'});            },            view_userPledge() {                this.$router.push({path: '/userPledge'});            },            /*my_account() {                this.$router.push({path: '/account'});            },*/            /*modify_user() {                this.$router.push({path: '/user_modify'});            },*/            get_login_user() {                this.$http.get('http://csse-s365.canterbury.ac.nz:5199/api/v2/users/'+this.$session.get('id'),                    {                        headers: {                            'X-Authorization': this.$session.get('token')                        }                    })                    .then(function (res) {                        this.login_user_id = res.body.id;                        this.login_user_email = res.body.email;                        this.login_user_location = res.body.location;                    })            },            to_user_modify() {                this.$router.push({path: '/user_modify'})            },            view_userPledge(){                this.$http.get('http://csse-s365.canterbury.ac.nz:5199/api/v2/projects?count=5&open=true&backer='+this.$session.get('id'))                    .then(function (res) {                        if(res.body.length==0){                            this.empty_flag=0;                            alert("No any pledge");                        }else{                            this.empty_flag=1;                            this.my_pledges=res.body;                            this.$router.push({path: '/userPledge'});                        }                    })            },            view_userProject(){                this.$http.get('http://csse-s365.canterbury.ac.nz:5199/api/v2/projects?count=5&open=true&creator='+this.$session.get('id'))                    .then(function (res) {                        if(res.body.length==0){                            this.empty_flag1=0;                        }else{                            this.empty_flag1=1;                            this.my_projects=res.body                            this.$router.push({path: '/userProject'});                        }                    })            }        }    }</script>