<template>    <div>        <header>            <div class="headline">                <a>                    <strong>FindHopes</strong>                </a>            </div>        </header>        <div class="button-back" style="margin-top:80px;"><router-link :to="{ name:'projects' }"><sgrong>Back</sgrong></router-link></div>        <!-- login  -->        <div class="userTitle">Login </div>        <div id="loginModal" class="login-site" role="dialog">            <div>                <div>                    <div>                        <form>                            <div class="labelInfo">                                <label class="labelInfo" for="newUsername">Username</label>                                <input type="text" class="text-username" id="newUsername" placeholder="Username" v-model="login_username">                                <small>Please enter 8-20 digits</small>                            </div>                            <div class="labelInfo">                                <label class="labelInfo" for="newPassword">Password</label>                                <input type="password" class="text-username" id="newPassword" placeholder="Password" v-model="login_pwd">                                <small>Please enter 8-20 digits</small>                            </div>                            <div class="labelInfo">                                <label class="form-check-label">                                    <input type="checkbox" v-model="remember_me"> Remember user                                </label>                            </div>                        </form>                    </div>                    <div>                        <div style="color: red; font-family: 'Times New Roman', Times, serif;">{{msg_error}}</div>                        <button type="button" class="button-login" data-dismiss="modal" @click="default_login_modal()">Cancel</button>                        <button type="button" class="button-login" @click="log_in()">Login</button>                    </div>                </div>            </div>        </div>        <router-view></router-view>    </div></template><script>    export default {        data() {            return {                sign_username: '',                sign_email: '',                sign_pwd: '',                sign_confirmpwd: '',                sign_location: '',                username_msg_color: '',                sign_username_msg: '',                email_msg_color: '',                sign_email_msg: '',                pwd_msg_color: '',                sign_pwd_msg: '',                confirmpwd_msg_color: '',                sign_confirmpwd_msg: '',                location_msg_color: '',                sign_location_msg: '',                flag_username: false,                flag_email: false,                flag_pwd: false,                flag_confirmpwd: false,                error_msg: '',                login_username:'',                login_pwd:'',                msg_error:'',                remember_me:0,                error_msg_color:'white',                delete_user_msg:'',                delete_username:'',            }        },        methods: {//same username check(did not add)            check_sign_username() {                if (this.sign_username == '') {                    this.username_msg_color = 'red';                    this.sign_username_msg = ' Username cannot be empty';                    document.getElementById("username_msg").className = 'col fa fa-exclamation-triangle';                    this.flag_username = false;                }else {                    this.username_msg_color = 'green';                    this.sign_username_msg = '';                    document.getElementById("username_msg").className = 'col fa fa-check';                    this.flag_username = true;                }            },            check_sign_email() {                let email_reg = /^[A-Za-z0-9]+([-_.][A-Za-z0-9]+)*@([A-Za-z0-9]+[-.])+[A-Za-z0-9]{2,5}$/; ;                if (this.sign_email == '') {                    this.email_msg_color = 'red';                    this.sign_email_msg = ' Email address cannot be empty';                    document.getElementById("email_msg").className = 'col fa fa-exclamation-triangle';                    this.flag_email = false;                } else if(!email_reg.test(this.sign_email)){                    this.email_msg_color = 'red';                    this.sign_email_msg = ' Incorrect Email address format';                    document.getElementById("email_msg").className = 'col fa fa-exclamation-triangle';                    this.flag_email = false;                }else {                    this.email_msg_color = 'green';                    this.sign_email_msg = '';                    document.getElementById("email_msg").className = 'col fa fa-check';                    this.flag_email = true;                }            },            check_sign_pwd() {                if (this.sign_pwd == '') {                    this.pwd_msg_color = 'red';                    this.sign_pwd_msg = ' Password cannot be empty';                    document.getElementById("pwd_msg").className = 'col fa fa-exclamation-triangle';                    this.flag_pwd = false;                } else if (this.sign_confirmpwd == this.sign_pwd) {                    this.pwd_msg_color = 'green';                    this.sign_pwd_msg = '';                    document.getElementById("pwd_msg").className = 'col fa fa-check';                    this.flag_pwd = true;                    this.confirmpwd_msg_color = 'green';                    this.sign_confirmpwd_msg = '';                    document.getElementById("confirmpwd_msg").className = 'col fa fa-check';                    this.flag_confirmpwd = true;                } else if (this.sign_confirmpwd == '') {                    this.pwd_msg_color = 'green';                    this.sign_pwd_msg = '';                    document.getElementById("pwd_msg").className = 'col fa fa-check';                    this.flag_pwd = true;                    this.flag_confirmpwd = false;                } else {                    this.pwd_msg_color = 'green';                    this.sign_pwd_msg = '';                    document.getElementById("pwd_msg").className = 'col fa fa-check';                    this.flag_pwd = true;                    this.confirmpwd_msg_color = 'red';                    this.sign_confirmpwd_msg = ' Confirm password must be same';                    document.getElementById("confirmpwd_msg").className = 'col fa fa-exclamation-triangle';                    this.flag_confirmpwd = false;                }            },            confirm_sign_pwd() {                if (this.sign_confirmpwd == '') {                    this.confirmpwd_msg_color = 'red';                    this.sign_confirmpwd_msg = ' Password cannot be empty';                    document.getElementById("confirmpwd_msg").className = 'col fa fa-exclamation-triangle';                    this.flag_confirmpwd = false;                } else if (this.sign_confirmpwd != this.sign_pwd) {                    this.confirmpwd_msg_color = 'red';                    this.sign_confirmpwd_msg = ' Confirm password must be same';                    document.getElementById("confirmpwd_msg").className = 'col fa fa-exclamation-triangle';                    this.flag_confirmpwd = false;                } else {                    this.confirmpwd_msg_color = 'green';                    this.sign_confirmpwd_msg = '';                    document.getElementById("confirmpwd_msg").className = 'col fa fa-check';                    this.flag_confirmpwd = true;                }            },            check_sign_location() {                this.location_msg_color = 'green';                this.sign_location_msg = '';                document.getElementById("location_msg").className = 'col fa fa-check';            },            default_signup_modal() {                this.sign_username = '';                this.sign_email = '';                this.sign_pwd = '';                this.sign_confirmpwd = '';                this.sign_location = '';                this.username_msg_color = '';                this.sign_username_msg = '';                this.email_msg_color = '';                this.sign_email_msg = '';                this.pwd_msg_color = '';                this.sign_pwd_msg = '';                this.confirmpwd_msg_color = '';                this.sign_confirmpwd_msg = '';                this.location_msg_color = '';                this.sign_location_msg = '';                document.getElementById("username_msg").className = 'col';                document.getElementById("email_msg").className = 'col';                document.getElementById("pwd_msg").className = 'col';                document.getElementById("confirmpwd_msg").className = 'col';                document.getElementById("location_msg").className = 'col';                this.flag_username = false;                this.flag_email = false;                this.flag_pwd = false;                this.flag_confirmpwd = false;                this.error_msg = '';                this.error_msg_color='white';            },            log_in() {                this.$http.post('http://csse-s365.canterbury.ac.nz:5199/api/v2/users/login?username='+this.login_username+'&password='+this.login_pwd)                    .then(function (res) {//                        console.log(res);                        if(res.status==200&&'token' in res.body){                            this.$session.start();                            this.$session.set('username', this.login_username);                            this.$session.set('id', res.body.id);                            this.$session.set('token', res.body.token);//                            Vue.http.headers.common['X-Authorization']=res.body.token;                            this.default_login_modal();                            window.sessionStorage.setItem('islogin',true);                            this.$router.push({path: '/account'});                        }                    }, function (error) {                        this.msg_error='Incorrect';                        this.login_username='';                        this.login_pwd='';                        this.remember_me=0;                    });            },            default_login_modal (){                this.login_username='';                this.login_pwd='';                this.msg_error='';                this.remember_me=0;            },        }    }</script>