    <template>        <div>            <header>                <div class="headline">                    <a>                        <strong>FindHopes</strong>                    </a>                </div>            </header>            <div class="button-back" style="margin-top:80px;"><router-link :to="{ name:'projects' }"><sgrong>Back</sgrong></router-link></div>            <!-- signup model -->            <div class="userTitle">Signup </div>            <div id="signupModal" class="login-site" style="height:400px" role="dialog">                <div role="content">                    <div >                        <div class="formInfo">                            <form id="register_form">                                <div class="labelInfo">                                    <label class="labelInfo" for="signupInputUsername">Username</label>                                    <input type="text" class="text-username" id="signupInputUsername" placeholder="Username" v-model="sign_username" @blur="check_sign_username()">                                    <span style="color: red">*</span>                                    <span id="username_msg" v-bind:style="{color:username_msg_color}">{{sign_username_msg}}</span>                                </div>                                <div class="labelInfo">                                    <label class="labelInfo" for="signupEmail">Email</label>                                    <input type="email" class="text-username" id="signupEmail" placeholder="Email" v-model="sign_email" @blur="check_sign_email()">                                    <span style="color: red">*</span>                                    <label id="email_msg" v-bind:style="{color:email_msg_color}">{{sign_email_msg}}</label>                                </div>                                <div class="labelInfo">                                    <label class="labelInfo" for="signupInputPassword">Password</label>                                    <input type="password" class="text-username" id="signupInputPassword" placeholder="Password" v-model="sign_pwd" @blur="check_sign_pwd()">                                    <span style="color: red">*</span>                                    <label id="pwd_msg" v-bind:style="{color:pwd_msg_color}">{{sign_pwd_msg}}</label>                                </div>                                <div class="labelInfo">                                    <label class="labelInfo" for="signupInputPassword2">Confirm Password</label>                                    <input type="password" class="text-username" id="signupInputPassword2" placeholder="Confirm Password" v-model="sign_confirmpwd " @blur="confirm_sign_pwd()">                                    <span style="color: red">*</span>                                    <label id="confirmpwd_msg" v-bind:style="{color:confirmpwd_msg_color}">{{sign_confirmpwd_msg}}</label>                                </div>                                <div class="labelInfo">                                    <label class="labelInfo" for="signupLocation">Location</label>                                    <input type="text" class="text-username" id="signupLocation" placeholder="Location" v-model="sign_location" @blur="check_sign_location()">                                    <label id="location_msg" v-bind:style="{color:location_msg_color}">{{sign_location_msg}}</label>                                </div>                            </form>                        </div>                        <div class="modal-footer">                            <div style="font-family: 'Times New Roman', Times, serif;" v-bind:style="{color:error_msg_color}">{{error_msg}}</div>                            <button type="button" class="button-login" @click="register()">Sign & Login</button>                            <button type="button" class="button-login" data-dismiss="modal" @click="default_signup_modal()">Cancel</button>                        </div>                    </div>                </div>            </div>            <router-view></router-view>        </div>    </template>    <script>        export default {            data() {                return {                    sign_username: '',                    sign_email: '',                    sign_pwd: '',                    sign_confirmpwd: '',                    sign_location: '',                    username_msg_color: '',                    sign_username_msg: '',                    email_msg_color: '',                    sign_email_msg: '',                    pwd_msg_color: '',                    sign_pwd_msg: '',                    confirmpwd_msg_color: '',                    sign_confirmpwd_msg: '',                    location_msg_color: '',                    sign_location_msg: '',                    flag_username: false,                    flag_email: false,                    flag_pwd: false,                    flag_confirmpwd: false,                    error_msg: '',                    login_username: '',                    login_pwd: '',                    msg_error: '',                    remember_me: 0,                    error_msg_color: 'white',                    delete_user_msg: '',                    delete_username: '',    //                session_username: this.$session.get('username'),    //                session_token: this.$session.get('token'),    //                session_id: this.$session.get('id'),                }            },            methods: {    //same username check(did not add)                check_sign_username() {                    if (this.sign_username == '') {                        this.username_msg_color = 'red';                        this.sign_username_msg = ' Username cannot be empty';                        document.getElementById("username_msg").className = 'col fa fa-exclamation-triangle';                        this.flag_username = false;                    } else {                        this.username_msg_color = 'green';                        this.sign_username_msg = '';                        document.getElementById("username_msg").className = 'col fa fa-check';                        this.flag_username = true;                    }                },    //email format check                check_sign_email() {                    let email_reg = /^[A-Za-z0-9]+([-_.][A-Za-z0-9]+)*@([A-Za-z0-9]+[-.])+[A-Za-z0-9]{2,5}$/;                    ;                    if (this.sign_email == '') {                        this.email_msg_color = 'red';                        this.sign_email_msg = ' Email address cannot be empty';                        document.getElementById("email_msg").className = 'col fa fa-exclamation-triangle';                        this.flag_email = false;                    } else if (!email_reg.test(this.sign_email)) {                        this.email_msg_color = 'red';                        this.sign_email_msg = ' Incorrect Email address format';                        document.getElementById("email_msg").className = 'col fa fa-exclamation-triangle';                        this.flag_email = false;                    } else {                        this.email_msg_color = 'green';                        this.sign_email_msg = '';                        document.getElementById("email_msg").className = 'col fa fa-check';                        this.flag_email = true;                    }                },                check_sign_pwd() {                    if (this.sign_pwd == '') {                        this.pwd_msg_color = 'red';                        this.sign_pwd_msg = ' Password cannot be empty';                        document.getElementById("pwd_msg").className = 'col fa fa-exclamation-triangle';                        this.flag_pwd = false;                    } else if (this.sign_confirmpwd == this.sign_pwd) {                        this.pwd_msg_color = 'green';                        this.sign_pwd_msg = '';                        document.getElementById("pwd_msg").className = 'col fa fa-check';                        this.flag_pwd = true;                        this.confirmpwd_msg_color = 'green';                        this.sign_confirmpwd_msg = '';                        document.getElementById("confirmpwd_msg").className = 'col fa fa-check';                        this.flag_confirmpwd = true;                    } else if (this.sign_confirmpwd == '') {                        this.pwd_msg_color = 'green';                        this.sign_pwd_msg = '';                        document.getElementById("pwd_msg").className = 'col fa fa-check';                        this.flag_pwd = true;                        this.flag_confirmpwd = false;                    } else {                        this.pwd_msg_color = 'green';                        this.sign_pwd_msg = '';                        document.getElementById("pwd_msg").className = 'col fa fa-check';                        this.flag_pwd = true;                        this.confirmpwd_msg_color = 'red';                        this.sign_confirmpwd_msg = ' Confirm password must be same';                        document.getElementById("confirmpwd_msg").className = 'col fa fa-exclamation-triangle';                        this.flag_confirmpwd = false;                    }                },                confirm_sign_pwd() {                    if (this.sign_confirmpwd == '') {                        this.confirmpwd_msg_color = 'red';                        this.sign_confirmpwd_msg = ' Password cannot be empty';                        document.getElementById("confirmpwd_msg").className = 'col fa fa-exclamation-triangle';                        this.flag_confirmpwd = false;                    } else if (this.sign_confirmpwd != this.sign_pwd) {                        this.confirmpwd_msg_color = 'red';                        this.sign_confirmpwd_msg = ' Confirm password must be same';                        document.getElementById("confirmpwd_msg").className = 'col fa fa-exclamation-triangle';                        this.flag_confirmpwd = false;                    } else {                        this.confirmpwd_msg_color = 'green';                        this.sign_confirmpwd_msg = '';                        document.getElementById("confirmpwd_msg").className = 'col fa fa-check';                        this.flag_confirmpwd = true;                    }                },                check_sign_location() {                    this.location_msg_color = 'green';                    this.sign_location_msg = '';                    document.getElementById("location_msg").className = 'col fa fa-check';                },                default_signup_modal() {                    this.sign_username = '';                    this.sign_email = '';                    this.sign_pwd = '';                    this.sign_confirmpwd = '';                    this.sign_location = '';                    this.username_msg_color = '';                    this.sign_username_msg = '';                    this.email_msg_color = '';                    this.sign_email_msg = '';                    this.pwd_msg_color = '';                    this.sign_pwd_msg = '';                    this.confirmpwd_msg_color = '';                    this.sign_confirmpwd_msg = '';                    this.location_msg_color = '';                    this.sign_location_msg = '';                    document.getElementById("username_msg").className = 'col';                    document.getElementById("email_msg").className = 'col';                    document.getElementById("pwd_msg").className = 'col';                    document.getElementById("confirmpwd_msg").className = 'col';                    document.getElementById("location_msg").className = 'col';                    this.flag_username = false;                    this.flag_email = false;                    this.flag_pwd = false;                    this.flag_confirmpwd = false;                    this.error_msg = '';                    this.error_msg_color = 'white';                },                register() {                    if (this.flag_username && this.flag_email && this.flag_pwd && this.flag_confirmpwd == true) {                        this.$http.post('http://csse-s365.canterbury.ac.nz:5199/api/v2/users',                            {                                "username": this.sign_username,                                "email": this.sign_email,                                "password": this.sign_confirmpwd,                                "location": this.sign_location                            }                        ).then(function (res) {                            this.login_username = this.sign_username;                            this.login_pwd = this.sign_confirmpwd;                            this.default_signup_modal();                            this.$http.post('http://csse-s365.canterbury.ac.nz:5199/api/v2/users/login?username=' + this.login_username + '&password=' + this.login_pwd)                                .then(function (res) {                                    if (res.status == 200 && 'token' in res.body) {                                        this.$session.start();                                        this.$session.set('username', this.login_username);                                        this.$session.set('id', res.body.id);                                        this.$session.set('token', res.body.token);                                        this.default_login_modal();                                        window.sessionStorage.setItem('islogin', true);                                        this.$router.push({path: '/account'});                                    }                                });                        }, function (error) {                            this.error_msg_color = 'red';                            this.error_msg = '' +                                'Username or email is not unique';                        });                    } else {                        this.error_msg_color = 'red';                        this.error_msg = 'Invalid typing';                    }                },                default_login_modal (){                    this.login_username='';                    this.login_pwd='';                    this.msg_error='';                    this.remember_me=0;                },            }        }    </script>