<template>    <div>        <header>            <div class="headline">                <a>                    <strong>FindHopes</strong>                </a>            </div>        </header>        <div class="button-back" style="margin-top:80px;"><router-link :to="{ name:'projects' }"><sgrong>Back</sgrong></router-link></div>        <div class="body">            <br>            <div class="container">                <br>                <div class="row">                    <div class="col-12">                        <h3>My project</h3>                        <hr>                    </div>                </div>                <div class="row-content row">                    <div class="col-12 col-lg-10">                        <h2>Overview</h2>                        <div class="table-responsive table-hover">                            <table class="table text-center">                                <thead class="thead-inverse">                                <tr class="row">                                    <th class="text-center col-2 col-lg-2">ID</th>                                    <th class="text-center col-3 col-lg-3">Title</th>                                    <th class="text-center col-3 col-lg-3">Subtitle</th>                                </tr>                                </thead>                                <tbody v-if="empty_flag==1" v-for="project in userProjects">                                <tr class="row">                                    <td class="col-2 col-lg-2">{{project.id}}</td>                                    <td class="col-3 col-lg-3">{{project.title}}</td>                                    <td class="col-3 col-lg-3">{{project.subtitle}}</td>                                    <td class="button-details">                                        <router-link :to="{name: 'project', params:{ projectId: project.id}}">                                            View                                        </router-link>                                    </td>                                </tr>                                </tbody>                                <tbody v-if="empty_flag==0">                                <tr>                                    <td class="row text-left"><h2 class="offset-1 col-8" style="font-family: Arial">Empty</h2><h6 class="offset-1 col-8"><router-link :to="{path: '/createProject'}">Create</router-link> a new project</h6></td>                                </tr>                                </tbody>                            </table>                        </div>                    </div>                </div>            </div>        </div>    </div></template><script>    export default {        data() {            return {                user_id:this.$session.get('id'),                login_username:this.$session.get('username'),                session_token: this.$session.get('token'),                userProjects: [],                empty_flag: 0            }        },        mounted: function () {            this.view_userProject();        },        methods: {            log_out() {//                console.log(this.$session.get('token'));                this.$http.post('http://csse-s365.canterbury.ac.nz:5199/api/v2/users/logout/',                    {},                    {                        headers:                            {                                'X-Authorization': this.$session.get('token')                            },                    },                ).then(function(res){                    this.$router.push({path: '/'});                    window.sessionStorage.setItem('islogin',false);                    this.$session.destroy();                }, function (err) {                    console.log(err);                })            },            createProject() {                this.$router.push({path: '/createProject'});            },            userProject() {                this.$router.push({path: '/userProject'});            },            my_pledge() {                this.$router.push({path: '/my_pledge'});            },            myAccount() {                this.$router.push({path: '/account'});            },            /*modify_user() {                this.$router.push({path: '/user_modify'});            },*/            view_userProject(){                this.$http.get('http://csse-s365.canterbury.ac.nz:5199/api/v2/projects?open=true&creator='+this.user_id)                    .then(function (res) {                        if(res.body.length==0){                            this.empty_flag=0;                        }else{                            this.empty_flag=1;                            this.userProjects=res.body;                        }                    })            },            /*iew_detail(pro_id) {                this.$session.set('pro_status', 'userProject');                this.$router.push({path: '/project_detail/'+ pro_id});            }*/        }    }</script>